'@

$icons = $svgData | ConvertFrom-Json

$outputDir = "c:\Users\boliv\Documents\Pilar\assets\svg-icons"

Write-Host "Extraindo $($icons.Count) √≠cones SVG..." -ForegroundColor Green

foreach ($icon in $icons) {
    $filename = "$($icon.name)-$($icon.index).svg"
    $filepath = Join-Path $outputDir $filename
    
    # Salva o conte√∫do SVG
    $icon.content | Out-File -FilePath $filepath -Encoding UTF8
    
    Write-Host "  ‚úì Salvou: $filename" -ForegroundColor Cyan
}

Write-Host "`n‚úÖ Extra√ß√£o conclu√≠da! $($icons.Count) √≠cones salvos em:" -ForegroundColor Green
Write-Host "   $outputDir" -ForegroundColor Yellow

# Cria um arquivo de invent√°rio
$inventory = @{
    total = $icons.Count
    extractedDate = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    icons = $icons | Select-Object index, name, viewBox
}

$inventoryPath = Join-Path $outputDir "inventory.json"
$inventory | ConvertTo-Json -Depth 10 | Out-File -FilePath $inventoryPath -Encoding UTF8

Write-Host "`nüìä Invent√°rio salvo em: inventory.json" -ForegroundColor Magenta
